mapper.select:
SELECT * from wecode.user_diary
where userId = #{userId}
Order By createTime Desc

mapper.update:
update wecode.user
set user.name = #{userName}
where user.id = #{userId}

update wecode.user
set user.introduction = #{userIntro}
where user.id = #{userId}

update wecode.user
set user.password = #{password}
where user.id = #{userId}

update wecode.user
set user.email = #{email}
where user.id = #{userId}

update wecode.user
set user.phoneNumber = #{phoneNumber}
where user.id = #{userId}

update wecode.user
set user.address = #{address}
where user.id = #{userId}

update wecode.user
set user.sex = #{sex}
where user.id = #{userId}

update wecode.user
set user.voluntarySchool = #{voluntarySchool}
where user.id = #{userId}

update wecode.user
set user.birthday = #{birthday}
where user.id = #{userId}

update wecode.user
set user.photo = #{photo}
where user.id = #{userId}
